import React, { useState } from "react";

export default function Comp1() {
  const [n1, setN1] = useState("");
  const [n2, setN2] = useState("");
  const [r, setR] = useState(0);

  const modif1 = (e) => {
    setN1(e.target.value);
  };

  const modif2 = (e) => {
    setN2(e.target.value);
  };

  const somme = () => {
    setR(Number(n1) + Number(n2));
  };

  return (
    <div>
      <h1>Calcul with state</h1>
      Nombre 1:{" "}
      <input type="text" id="n1" onChange={(e) => modif1(e)} value={n1} />{" "}
      &nbsp; Nombre 2:{" "}
      <input type="text" id="n2" onChange={(e) => modif2(e)} value={n2} />{" "}
      &nbsp;
      <input type="button" value="Somme" onClick={somme} />
      <br />
      <br />
      RÃ©sultat:{" "}
      <input type="text" id="r" value={r} />
    </div>
  );
}
