import React from "react";

export default function Comp1() {
 const somme =()=>{
  let s=Number(  document.getElementById.value("n1") + document.getElementById.value("n2")) ;
  return s

 }

   const handleSommeClick = () => {
     document.getElementById("r").value = somme();
   };
  return (
    <div>
      <h1>calcul without state</h1>
      nombre 1 : <input type="text" id="n1" /> &nbsp; nombre 2 :{" "}
      <input type="text" id="n2" />
      <input type="button" value="somme" onClick={() => handleSommeClick} />
      <br />
      <br />
      Resultat : <input type="text" id="r" value={somme()} />
    </div>
  );
}
