import React, { useState } from "react";

export default function Comp1({ titre, text, fparent }) {
  const handleAlert = () => {
    window.alert("mchii t7awa");
  };

  // Declaration d'un state counter:
  const [counter, setCounter] = useState(10);

  // Declaration de la methode increment et decrement:
  const inc = () => {
    let c = counter;
    c++;
    setCounter(c);
  };

  const dec = () => {
    let c = counter;
    c--;
    setCounter(c);
    if (c === 0) {
      c = 0;
      setCounter(c);
    }
  };

  return (
    <div>
      <div className="card" style={{ width: "18rem" }}>
        <img
          className="card-img-top"
          src="https://source.unsplash.com/random/200x200?sig=1"
          alt="Card cap"
        />
        <div className="card-body">
          <h5 onClick={() => fparent(7)} className="card-title">
            {titre}
          </h5>
          <p className="card-text">{text}</p>
          <a onClick={handleAlert} href="#" className="btn btn-primary">
            submit
          </a>
        </div>
      </div>
      La valeur du state counter est <h1>{counter}</h1>
      <br />
      <input type="button" value="+" onClick={inc} />
      <input type="button" value="-" onClick={dec} />
    </div>
  );
}
