import React, { useState } from "react";

export default function Comp1() {
  const [nom, setNom] = useState("");

  const modifierNom=(e)=>{
    setNom(e.target.value);
    console.log(nom)
  }
  return (
    <div>
      votre nom : <input type="text"  onChange={(e)=>modifierNom(e)}    value={nom} />
    </div>
  );
}
