import React, { useState } from "react";

export default function Comp1() {
  const[n1,setN1]=useState();
  const[n2,setN2]=useState();
  const[r,setR]=useState(0);
  


  const modif1=(e)=>{
setN1(e.target.value); 
  }
  const modif2=(e)=>{
setN2(e.target.value); 
  }
  return (
    <div>
      <h1>calcul with state</h1>
      nombre 1 : <input type="text" id="n1"onChange={(e)=>modif1(e)} value={n1}/> &nbsp; 
      nombre 2 : <input type="text" id="n2"  value={n2}/> &nbsp; 
      <input type="button" value="somme"  />
      <br />
      <br />
      Resultat : <input type="text" id="r" value={r} />
    </div>
  );
}
