import React, { useState } from "react";

export default function Comp1() {
  const [toggle, setToggle] = useState(true);

  const handleChange = () => {
    setToggle(!toggle);
  };

  let content = toggle ? <img src="./img/3db0b7792d6d4cf3a2fba6b069ac805f.png"/> : <img src=""/>

  return (
    <div>
      {content}
      <input type="button" onClick={handleChange} value="Change" />
    </div>
  );
}
